import PropTypes from "prop-types";
import Button from "./Button";

const Header = ({ title, sortbytitle, sortbyauthor }) => {
  return (
    <header className="header">
      <h1>{title}</h1>
      <Button color="blue" text="Sort by Title" onClick={sortbytitle} />
      <Button color="blue" text="Sort by Author" onClick={sortbyauthor} />
    </header>
  );
};

Header.defaultProps = {
  title: "Booklist",
};

export default Header;
