import "./App.css";

import { BrowserRouter as Router, Route, Routes } from "react-router-dom";

import { useState, useEffect } from "react";
import Books from "./components/Books";
import Header from "./components/Header";
import AddBook from "./components/AddBook";
import { Link } from "react-router-dom";

function App() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const getBooks = async () => {
      const booksFromServer = await fetchBooks();
      setBooks(booksFromServer);
    };

    getBooks();
  }, []);

  //Fetch Books
  const fetchBooks = async () => {
    const res = await fetch("http://localhost:5000/books");
    const data = await res.json();
    console.log(data);
    return data;
  };

  //sort by title
  const sortbytitle = async () => {
    const booksFromServer = await fetchBooks();
    booksFromServer = booksFromServer.sort((a, b) => {
      if (a.title < b.title) {
        return -1;
      }
    });
    console.log(booksFromServer);
  };

  // Add Book
  const addBook = async (book) => {
    const res = await fetch("http://localhost:5000/books", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(book),
    });

    const data = await res.json();

    setBooks([...books, data]);
  };

  //delete book
  const deleteBook = async (id) => {
    await fetch(`http://localhost:5000/books/${id}`, {
      method: "DELETE",
    });

    //erstellt neues array ohne der uebergenen i
    setBooks(books.filter((book) => book.id !== id));
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <div className="container">
              <Header
                sortbyauthor={() => {
                  console.log("a");
                }}
                sortbytitle={() => {
                  console.log("b");
                }}
              />
              {books.length > 0 ? (
                <Books books={books} onDelete={deleteBook} />
              ) : (
                "No Books to show"
              )}
              <div className="right-align">
                <Link to="/addBook" className="btn">
                  Add Book
                </Link>
              </div>
            </div>
          }
        />
        <Route path="/addBook" element={<AddBook onAdd={addBook} />} />
      </Routes>
    </Router>
  );
}

export default App;
