import React, { useState } from 'react';
import Select from 'react-select';
import createIcon from '../icons/plus-circle.svg';

const AppointmentForm = ({ onSubmit, initialData, backendError }) => {
  const [courseID, setCourseID] = useState(initialData ? initialData.courseID : '');
  const [date, setDate] = useState(initialData ? initialData.date : '');
  const [availability, setAvailability] = useState(initialData ? initialData.availability : true);

  // Error state for date validation
  const [dateError, setDateError] = useState('');

  const [courseError, setCourseError] = useState(''); // State for course validation

  const courses = ['1w e', '1p_b', '1d1', 'Tel', '1kul', 'Seku2', 'leth1', '1g1', '1sk1', '1gen', '1m1', '161', '1ph2', '1sW H', '1p_e', '1d2', '1e2', '1its2', '1ku2', '1eth', '12', '1sk2', '1wr?', '1m2', '162', '15W FB', '1w geo', '1p_ph', '1d3', '1e3', '1mu1', 'lev1', '1g3', '1sk3', '1m3', '1b3', '1w m', '1d4', '104', 'lits1', '1mu2', '1k1', '1g4', '1sk4', '1m4', '1ph1', 'Sinf', 'TSW V', '1w g', '1ku3', '1k2', '1wr1', '5voc', '1sps1', '1w mu', '1p_mu', '15-11', '1sW a', '1w b', '511', '1p m', '1c1', '1w d', '1F1', '5psy1', '1c2', '15W Gy', '5psy2', '1p_d', '1p_t', '1p 1', 'Sekol','7eth2', '204', '2d2', '2d3', '2d4', '2e1', '2e3', '2geo1', '2geo2', '2g1', '2g2', '2g4', '2k1', '2k2', '2phA1', '2m1', '2m2', '2m3', '2m4','2p_d', '2p_e', '2p_sk', '2p k', '2p mu', '2p sBa', '2p sHu', '2ph2', '2r 2', '2s-t1', '2sWa', '2sW Ba', '2sW Gy', '2sW a', '2w b', '2w e','2p_d', '2p_e', '2p_sk', '2p k', '2p mu', '2p sBa', '2p sHu', '2ph2', '2r 2', '2s-t1', '2sWa', '2sW Ba', '2sW Gy', '2sW a', '2w b', '2w e'];

  const prohibitedDates = ['2024-03-26','2024-04-01','2024-05-01','2024-03-25']; // Feiertage und Ferientage
  
  const isFavoritAppointmentExists = () => {
    // Check if an existing "favorit" appointment exists for the selected course
    const existingFavoritAppointment = appointments.find(
      (appointment) => appointment.courseID === courseID &&
       appointment.availability &&
       appointment.date !== date
    );

    return !!existingFavoritAppointment;
  };

  const isValidDate = (selectedDate) => {
    const dayOfWeek = selectedDate.getDay();
    // Check if the day is not a weekend (Saturday: 6, Sunday: 0)
    return dayOfWeek !== 0 && dayOfWeek !== 6;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate the selected date
    const selectedDate = new Date();
    const formattedSelectedDate = selectedDate.toISOString().split('T')[0];;

    if (prohibitedDates.includes(formattedSelectedDate)) {
      // Display a popup error for prohibited dates
      alert('Termine können an diesem Datum nicht erstellt werden.');
      return;
    }

    // Validate the selected date for weekends
    if (!isValidDate(selectedDate)) {
      // Display a popup error for weekends
      alert('Termine können nicht an Wochenenden erstellt werden.');
      return;
    }

    // Check if an existing "favorit" appointment exists for the selected course
    if (availability === 'favorit' && isFavoritAppointmentExists()) {
      setDateError('Ein "favorit" Termin für diesen Kurs existiert bereits.'); // Clearer error message
      return;
    }

    // Validate the selected date
    const currentDate = new Date();

    if (!date || selectedDate < currentDate) {
      setDateError('Bitte wählen Sie ein zukünftiges Datum aus.');
      return;
    } else {
      setDateError('');
    }

    // Validate the selected course
    if (!courseID) {
      setCourseError('Bitte wählen Sie einen Kurs aus.');
      return;
    } else {
      setCourseError('');
    }

    // Check if the date is not selected
    if (!date) {
      setDateError('Bitte wählen Sie einen Datum aus.'); // Set error message
      return; // Stop form submission
    } else {
      setDateError(''); // Clear error message if date is selected
    }

    // Ensure the date is in the format expected by the backend (YYYY-MM-DD)
    const formattedDate = new Date(date).toISOString().split('T')[0];

    onSubmit({ courseID, date: formattedDate, availability });

    // Clear form fields after submission
    setCourseID('');
    setDate('');
    setAvailability(true);
  };

  return (
    <form onSubmit={handleSubmit} >
        <label>
  
          <Select
            value={{ value: courseID, label: courseID === '' ? 'Wählen Sie ein Kurs' : courseID }}
            options={courses.map((course) => ({ value: course, label: course }))}
            onChange={(selectedOption) => setCourseID(selectedOption.value)}
            isSearchable 
            placeholder="Wählen Sie ein Kurs"
          />
        </label>
      <label style={{ marginBottom: '10px', marginRight: '20px' }}>
        Datum:
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          style={{ display: 'inline-block', marginLeft: '5px' }}
        />
      </label>

      <label style={{ marginBottom: '10px', marginRight: '20px' }}>
        Verfügbarkeit:
        <label style={{ marginLeft: '10px' }}>
          <input
            type="radio"
            value="favorit"
            checked={availability}
            onChange={() => setAvailability(true)}
          />
          Favorit
        </label>
        <label style={{ marginLeft: '10px' }}>
          <input
            type="radio"
            value="geblockt"
            checked={!availability}
            onChange={() => setAvailability(false)}
          />
          Geblockt
        </label>
      </label>

      <button
        type="submit"
        style={{
          border: 'none',
          background: 'none',
          padding: 0,
          cursor: 'pointer',
          marginLeft: '10px', // Add some space between the button and the previous element
        }}
      >
        <img
          src={createIcon}
          alt="Create Icon"
          style={{ width: '30px', height: '30px' }}
        />
        Termin erstellen
      </button>

      {/* Display backend error if present */}
      {backendError && (
        <div style={{ color: 'red', marginTop: '10px' }}>
          {backendError}
        </div>
      )}

      {/* Display error message if date or course is not selected */}
      {dateError && <p style={{ color: 'red', marginTop: '10px' }}>{dateError}</p>}
      {courseError && <p style={{ color: 'red', marginTop: '10px' }}>{courseError}</p>}
    </form>
  );
};

export default AppointmentForm;
